mapper.update:
INSERT INTO user_auth
(
  phone,
  code
)
VALUES
(
  #{phone_number},
  #{auth_code}
)
ON DUPLICATE KEY UPDATE
code = #{auth_code}

mapper.select:
SELECT phone
      , code
FROM user_auth
WHERE phone = #{phone_number}
AND code = #{code}

mapper.update:
UPDATE user
SET password = #{new_password}
  , is_retry = 1
WHERE email = #{email}
